function initCountDown(e) { x = setInterval(function () { let t = (new Date).getTime(), n = e - t, o = { days: n / 864e5, hours: n % 864e5 / 36e5, minutes: n % 36e5 / 6e4, seconds: n % 6e4 / 1e3 }; for (let e in o) for (let t of document.getElementsByClassName(e)) t.innerText = Math.floor(o[e]); n < 0 && clearInterval(x) }, 1e3) }